(function(){"use strict";angular.module("app").controller("changePasswordController",changePasswordController);changePasswordController.$inject=["$rootScope","$scope","$location","growl","AuthService"];function changePasswordController($rootScope,$scope,$location,growl,AuthService){var vm=this;vm.submit=function(){AuthService.changePassword($rootScope.id,$rootScope.tokenSession,vm.current,vm.new).then(complete).catch(failed);function complete(data){var config={referenceId:1};if(data.state==1){growl.success(data.msg,config)}else{if(data.state==99){$location.path("/")}else{growl.error(data.msg,config)}}}function failed(data){console.log(data)}}}})();