(function(){"use strict";angular.module("app").controller("rechargeController",rechargeController);rechargeController.$inject=["$rootScope","$scope","$sce","GameAccount"];function rechargeController($rootScope,$scope,$sce,GameAccount){var vm=this;init();function init(){GameAccount.getProfile($rootScope.id,$rootScope.tokenSession).then(complete).catch(failed);function complete(response){vm.code=response.dato.numDoc;vm.name=response.dato.pNom+" "+response.dato.pApe;vm.email=response.dato.email;vm.options={height:30,width:1,displayValue:false,font:"monospace",textAlign:"center",fontSize:10,backgroundColor:"#ffffff",lineColor:"#000000"}}function failed(data){console.log(data)}}vm.printCard=function(printCardId){var innerContents=document.getElementById(printCardId).innerHTML;var popupWinindow=window.open("","_blank","width=500,height=500,scrollbars=no,menubar=no,toolbar=no,location=no,status=no,titlebar=no");popupWinindow.document.open();popupWinindow.document.write('<html><head><link rel="stylesheet" type="text/css" href="../app/css/style-print.css" /></head><body class="recharge-card" onload="window.print()">'+innerContents+"</html>");popupWinindow.document.close()}}})();