(function(){"use strict";angular.module("app").controller("verifyAccountController",verifyAccountController);verifyAccountController.$inject=["$scope","$routeParams","AuthService"];function verifyAccountController($scope,$routeParams,AuthService){var vm=this;vm.mail=$routeParams.mail;AuthService.verifyAccount(vm.mail).then(complete).catch(failed);function complete(data){vm.state=data.state;if(data.state==1){vm.title="Proceso Completado"}else{vm.title="Advertencia"}vm.detail=data.msg;console.log(data)}function failed(data){console.log(data)}}})();